<style lang="css">
	.navbar {
		@apply w-20 fixed hidden min-h-screen;
		@apply md:block md:sticky;
		background-color: var(--verde_primario);
		transition: width 200ms ease;
	}

	.navbar:hover .logo .nav-link {
		@apply grid place-items-center p-0 filter-none;
		background-color: var(--verde_oscuro);
	}

	.logo .nav-link span {
		@apply text-center text-2xl hidden;
		font-family: var(--letra_logo);
	}

	.navbar:hover .logo .nav-link span {
		@apply block;
	}

	.navbar-nav {
		@apply flex list-none flex-col h-full;
	}

	.nav-item {
		@apply text-center w-full;
	}

	.nav-item:last-child {
		@apply mt-auto;
	}

	.nav-link {
		@apply flex items-center h-20 duration-200;
		color: var(--blanco_fondo);
		text-decoration: none;
		filter: grayscale(100%) opacity(0.7);
	}

	.nav-link:hover {
		filter: grayscale(0%) opacity(1);
		background: var(--verde_oscuro);
	}

	.link-text {
		@apply hidden text-left mr-6;
	}

	.nav-link svg {
		min-width: 2rem;
		margin: 0 1rem;
	}

	.navbar:hover .link-text {
		@apply block;
	}
</style>
